arguments.argument:
x = view_xview[0] + global.ConsoleWindowX;
y = view_yview[0] + global.ConsoleWindowY;

var listlen, listheight, i, entryheight, curheight, str;
sep = string_height("_");
listlen = ds_list_size(global.ConsoleLog);
listheight = 0;
for (i = 0; i < listlen; i += 1) {
    str = ds_list_find_value(global.ConsoleLog, i);
    str = string_replace_all(str, '#', '\#');
    listheight += string_height_ext(str, -1, console_width);
}

entryheight = 12;

console_height = entryheight + listheight;

draw_set_alpha(0.75);
draw_set_color(c_black);
draw_rectangle(x - padding, y - listheight - entryheight - padding, x + console_width + padding, y + padding, false);

draw_set_color(c_white);
// left border mouseover highlight
if (x - padding <= mouse_x && mouse_x < x && y - console_height <= mouse_y && mouse_y < y) {
    draw_rectangle(x - padding, y - console_height, x, y, false);
// right border mouseover highlight
} else if (x + console_width <= mouse_x && mouse_x < x + console_width + padding && y - console_height <= mouse_y && mouse_y < y) {
    draw_rectangle(x + console_width, y - console_height, x + console_width + padding, y, false);
}

draw_set_alpha(1);
draw_surface(surface, x, y - entryheight);

draw_set_valign(fa_top);
draw_set_halign(fa_left);
curheight = 0;
for (i = 0; i < listlen; i += 1) {
    str = ds_list_find_value(global.ConsoleLog, i);
    str = string_replace_all(str, '#', '\#');
    draw_text_ext(x, y - listheight - entryheight + curheight, str, -1, console_width);
    curheight += string_height_ext(str, -1, console_width);
}
