arguments.argument:
if (string_length(keyboard_string) > 1) {
    var textbgn, textend;
    textbgn = string_copy(text, 0, seek);
    if (ovr) {
        textend = string_copy(text, seek+2, string_length(text) - seek - 1);
    } else {
        textend = string_copy(text, seek+1, string_length(text) - seek);
    }
    text = textbgn + string_copy(keyboard_string, 2, string_length(keyboard_string) - 1) + textend;
    _ConsoleDoSeek(seek + string_length(keyboard_string) - 1);
} else if (string_length(keyboard_string) < 1 && seek > 0) {
    var textbgn, textend;
    textbgn = string_copy(text, 0, seek - 1);
    textend = string_copy(text, seek + 1, string_length(text) - seek);
    text = textbgn + textend;
    _ConsoleDoSeek(seek - 1);
}
if (string_length(keyboard_string) != 1) {
    keyboard_string = "_";
    draw_cursor = true;
    cursorflash = 0;
}
if (rheldtime > 0 && seek < string_length(text)) {
    rheldtime += 1;
    if (rheldtime > 15) {
        _ConsoleDoSeek(seek + 1);
        draw_cursor = true;
        cursorflash = 0;
    }
}
if (lheldtime > 0 && seek > 0) {
    lheldtime += 1;
    if (lheldtime > 15) {
        _ConsoleDoSeek(seek - 1);
        draw_cursor = true;
        cursorflash = 0;
    }
}
if (dheldtime > 0) {
    dheldtime += 1;
    if (dheldtime > 15) {
        var textbgn, textend;
        textbgn = string_copy(text, 0, seek);
        textend = string_copy(text, seek + 2, string_length(text) - seek - 1);
        text = textbgn + textend;
        draw_cursor = true;
        cursorflash = 0;
    }
}
cursorflash += 1;
if (cursorflash == 16) {
    cursorflash = 0;
    draw_cursor = !draw_cursor;
}

// dragging
if (dragging) {
    global.ConsoleWindowX = dragstart_winx + (mouse_x - dragstart_mousex);
    global.ConsoleWindowY = dragstart_winy + (mouse_y - dragstart_mousey);
// resizing from left
} else if (resizing_x1) {
    if (resizestart_width + (resizestart_x - global.ConsoleWindowX) > 0) {
        global.ConsoleWindowX = resizestart_x + (mouse_x - resizestart_mousex);
        console_width = resizestart_width + (resizestart_x - global.ConsoleWindowX);
    } else {
        console_width = 1;
    }
    surface_free(surface);
    surface = surface_create(console_width, 12);
// resizing from right
} else if (resizing_x2) {
    if (resizestart_width + (mouse_x - resizestart_mousex) > 0) {
        console_width = resizestart_width + (mouse_x - resizestart_mousex);
    } else {
        console_width = 1;
    }
    surface_free(surface);
    surface = surface_create(console_width, 12);
}

// do surface drawing at end step to avoid screwing up normal drawing
surface_set_target(surface);
draw_clear_alpha(c_black, 0);

draw_set_alpha(0.75);
draw_set_color(c_gray);
draw_rectangle(0, 0, console_width, 12, false);

draw_set_color(c_orange);
draw_set_valign(fa_top);
draw_set_halign(fa_left);
draw_set_alpha(1);

var visible_string, visible_string_width;
visible_string = string_copy(text, 0, seek);
visible_string_width = string_width(visible_string);

var str;
str = string_replace_all(text, '#', '\#');
draw_text(0 - viewport_x, 0, str);

if (draw_cursor) {
    var cx, cy, temp;
    temp = text + " ";
    cx = visible_string_width - viewport_x;
    if (cx < 1) cx = 1;
    if (cx >= console_width) cx = console_width - 1;
    cy = string_height(text + " ") - string_height("_");
    draw_set_color(c_white);
    draw_line(cx, cy, cx, cy + string_height("_"));
}

surface_reset_target();
