arguments.argument:
if(not nocreate) {
    socket_destroy(global.serverSocket);
    ds_list_destroy(global.players);
    buffer_destroy(global.deserializeBuffer);
    buffer_destroy(downloadMapBuffer);

    with(Player)
        instance_destroy();

    with(PlayerControl)
        instance_destroy();

    room_goto_fix(returnRoom);
    if (global.serverPluginsInUse) {
        if (show_message_ext("Because you used this server's plugins, you will have to restart GG2 to play on another server.","Restart","","Quit") == 1) {
            execute_program(parameter_string(0), "-restart", false);   
        }
        game_end();
    }
}
