arguments.argument:
var redIsCapping, blueIsCapping;

redIsCapping = !instance_exists(IntelligenceBlue);
blueIsCapping = !instance_exists(IntelligenceRed);
if (!redIsCapping)
    redIsCapping = (IntelligenceBlue.x != IntelligenceBaseBlue.x or IntelligenceBlue.y != IntelligenceBaseBlue.y);
if (!blueIsCapping)
    blueIsCapping = (IntelligenceRed.x != IntelligenceBaseRed.x or IntelligenceRed.y != IntelligenceBaseRed.y);

if (timer > 0)
    timer -= 1;

if ((global.redCaps >= global.caplimit) or (global.blueCaps >= global.caplimit) and !overtime) {
    if (global.redCaps > global.blueCaps)
        global.winners = TEAM_RED;
    else if (global.blueCaps > global.redCaps)
        global.winners = TEAM_BLUE;
}
if (timer <= 0 and global.winners == -1)
    overtime = true;
if (overtime and !startedOvertime) {
    //Overtime rules... Overtime should only last one point
    if (global.redCaps > global.blueCaps){ //Blue can only stalemate; red can win
        blueCanWin = false;
        redCanWin = true;
    }
    if (global.blueCaps > global.redCaps){ //Red can only stalemate; blue can win
        redCanWin = false;
        blueCanWin = true;
    }
    else if (global.blueCaps == global.redCaps) { //Its anyone's game
       redCanWin = true;
       blueCanWin = true;
    }
    startedOvertime = true;
}
if (overtime) {
    if (redCanWin and !blueCanWin){
        if (global.redCaps >= global.blueCaps + 2 or !blueIsCapping)
            global.winners = TEAM_RED;
        if (global.redCaps == global.blueCaps)
            global.winners = TEAM_SPECTATOR;
    }
    else if (blueCanWin and !redCanWin) {
        if (global.blueCaps >= global.redCaps + 2 or !redIsCapping)
            global.winners = TEAM_BLUE;
        if (global.blueCaps == global.redCaps)
            global.winners = TEAM_SPECTATOR;
    }
    else if (blueCanWin and redCanWin){
        if (global.redCaps > global.blueCaps)
            global.winners = TEAM_RED;
        if (global.blueCaps > global.redCaps)
            global.winners = TEAM_BLUE;
        if (!redIsCapping and !blueIsCapping)
            global.winners = TEAM_SPECTATOR;
    }

}
